(#{memberId}, #{subject}, #{content}

	, #{secret}, #{password}

)

mapper.update:
update qna_board
set
subject = #{subject}, content = #{content}
where qna_board_id =
#{qnaBoardId}

update qna_comment
set
content = #{content}
where comment_id = #{commentId}

mapper.select:
select count(b.qna_board_id) from qna_board b
where b.subject like concat('%', #{word}, '%')
where ${key} = #{word}
